import React, { useState } from "react";
import Navbar from "../navbar/Navbar";
import BookReview from "./BookReview";

export default function Books() {
  const [showReview, setShowReview] = useState(false);

  const openReview = () => {
    setShowReview(true);
  };

  const closeReview = () => {
    setShowReview(false);
  };

  return (
    <div>
      <Navbar />
      <h1>Books</h1>
      <button onClick={openReview}>Book-review</button>
      {showReview && <BookReview onClose={closeReview} />}
    </div>
  );
}
