import React, { useState } from "react";
import "./Signup.css";

export default function Signup({setShowLogin}){
    const [isPasswordShown, setPasswordShown] = useState(false);

    function OpenLogin() {
        setShowLogin(true);
        console.log("going to Login");
    }

    function togglePasswordVisibility() {
        setPasswordShown(!isPasswordShown);
    }

    return(
        <div className="signup-card">
            <h1>Signup</h1>
            <div className="txt_field">
                <label htmlFor="username">Username: </label>
                <input type="email" name="" id="" />
            </div>
            <div className="txt_field">
                <label htmlFor="email">Email: </label>
                <input type="email" name="" id="" />
            </div>
            <div className="txt_field">
                <label htmlFor="password">Password: </label>
                <input type={`${isPasswordShown ? "text" : "password"}`} name="" id="" />
                <button onClick={togglePasswordVisibility}>Show Password</button>
            </div>
            <div className="txt_field">
                <label htmlFor="password">Confirm-Password: </label>
                <input type="password" name="" id="" />
            </div>
            <div className="buttons">
                <button onClick={OpenLogin}>Signup</button>
                <button onClick={OpenLogin}>go-to-Login</button>
            </div>
        </div>
    );
}
